function populatePhraseData(){$.ajax({type:"GET",url:"/data",success:function(a){a.map(function(a){switch(console.log(a),a.key){case"adjectives":adj=a.values;break;case"modadjectives":modadj=a.values;break;case"topics":topics=a.values}}),console.log(adj),console.log(modadj),console.log(topics)},error:function(){console.log("ERROR")}})}function generatePhrase(){var a=modadj[getRandomNum(0,modadj.length-1)],b=adj[getRandomNum(0,adj.length-1)],c=topics[getRandomNum(0,topics.length-1)];return a+" "+b+" "+c}function getRandomNum(a,b){return Math.round(Math.random()*(b-a)+a)}var adj=[],modadj=[],topics=[];$(document).ready(function(){populatePhraseData(),$("#generatePhraseBtn").on("click",function(){$(".phrase").remove();var a="<h2 class='phrase'>"+generatePhrase()+"</h2>";$(".phrase-wrapper").append(a)})});